!function(e) {
    e("html").removeClass("no-js"), e("header a").click(function(t) {
        if (!e(this).hasClass("no-scroll")) {
            t.preventDefault();
            var i = e(this).attr("href"),
                n = e(i).offset().top;
            e("html, body").animate({
                scrollTop: n + "px"
            }, Math.abs(window.pageYOffset - e(i).offset().top) / 1), e("header").hasClass("active") && e("header, body").removeClass("active")
        }
    }), e("#to-top").click(function() {
        e("html, body").animate({
            scrollTop: 0
        }, 500)
    }), e("#lead-down span").click(function() {
        var t = e("#lead").next().offset().top;
        e("html, body").animate({
            scrollTop: t + "px"
        }, 500)
    }), e("#mobile-menu-open").click(function() {
        e("header, body").addClass("active")
    }), e("#mobile-menu-close").click(function() {
        e("header, body").removeClass("active")
    })
}(jQuery);